import React, { useState } from 'react';
import './App.css';
import LoginPage from './LoginPage'; // 引入登录组件

// 原有的 CryptoDB 面板内容，我们保留在 App.js 中，但不直接渲染
function CryptoDBPanel() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState('');

  const handleSecureQuery = () => {
    if (!query.trim()) {
      alert('请输入查询');
      return;
    }
    setResults(`[模拟加密查询结果]\n查询内容：${query}`);
  };

  const handleClearInput = () => {
    setQuery('');
    setResults('');
  };

  const handleDecryptResults = () => {
    if (!results.trim()) {
      alert('没有可解密内容');
      return;
    }
    setResults(`[模拟解密结果]\n原始：${results}\n解密后：模拟数据`);
  };

  return (
    <div className="container">
      <div className="logo-container">
        <div className="logo"></div>
        <div className="logo-text">CryptoDB UI</div>
      </div>

      <div className="main-content">
        <div className="query-section">
          <div className="panel">
            <div className="panel-title">Encrypted Query Input</div>
            <textarea
              className="query-input"
              placeholder="输入加密SQL查询..."
              value={query}
              onChange={(e) => setQuery(e.target.value)}
            ></textarea>
            <button className="btn btn-secure" onClick={handleSecureQuery}>
              Secure Query
            </button>
            <button className="btn btn-clear" onClick={handleClearInput}>
              Clear Input
            </button>
          </div>

          <div className="panel">
            <div className="panel-title">Query Results (Encrypted)</div>
            <div className="query-results">{results}</div>
            <button className="btn btn-decrypt" onClick={handleDecryptResults}>
              Decrypt Results
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // 控制是否已登录

  const handleLoginSuccess = () => {
    setIsLoggedIn(true); // 登录成功，显示主界面
  };

  return (
    <div>
      {isLoggedIn ? (
        <CryptoDBPanel /> // 登录后显示主界面
      ) : (
        <LoginPage onLoginSuccess={handleLoginSuccess} /> // 否则显示登录页
      )}
    </div>
  );
}

export default App;
