import React, { useState, useEffect } from 'react';
import './DataOwnerPanel.css';

function DataOwnerPanel({ onLogout }) {
  const [tableData, setTableData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedTable, setSelectedTable] = useState('user_credit');
  const [syncStatus, setSyncStatus] = useState('Idle');

  // 模拟多个数据库表的数据
  const databaseTables = {
    user_credit: [
      { ID: "1", credit_rank: 6, income: 100000, age: 20 },
      { ID: "2", credit_rank: 5, income: 90000, age: 19 },
      { ID: "3", credit_rank: 6, income: 89700, age: 32 },
      { ID: "4", credit_rank: 6, income: 607000, age: 30 },
      { ID: "5", credit_rank: 5, income: 30070, age: 25 },
      { ID: "6", credit_rank: 6, income: 12070, age: 28 }
    ],
    user_profiles: [
      { ID: "U001", name: "张三", email: "zhangsan@email.com", role: "Admin", status: "Active" },
      { ID: "U002", name: "李四", email: "lisi@email.com", role: "User", status: "Active" },
      { ID: "U003", name: "王五", email: "wangwu@email.com", role: "Editor", status: "Inactive" }
    ],
    transactions: [
      { ID: "T001", amount: 1500, type: "Deposit", date: "2024-01-15", status: "Completed" },
      { ID: "T002", amount: 250, type: "Withdrawal", date: "2024-01-14", status: "Pending" },
      { ID: "T003", amount: 3000, type: "Transfer", date: "2024-01-13", status: "Completed" }
    ]
  };

  // 获取表头信息
  const getTableHeaders = (tableName) => {
    if (databaseTables[tableName] && databaseTables[tableName].length > 0) {
      return Object.keys(databaseTables[tableName][0]);
    }
    return [];
  };

  // 模拟从后端获取数据
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        // 模拟网络延迟
        await new Promise(resolve => setTimeout(resolve, 500));
        setTableData(databaseTables[selectedTable] || []);
        setLoading(false);
      } catch (err) {
        setError('获取数据失败');
        setLoading(false);
      }
    };

    fetchData();
  }, [selectedTable]);

  const handleUpload = () => {
    setSyncStatus('Uploading...');
    // 模拟上传过程
    setTimeout(() => {
      setSyncStatus('Completed');
      setTimeout(() => setSyncStatus('Idle'), 2000);
    }, 3000);
  };

  const handleSyncSettings = () => {
    alert('打开同步设置...');
  };

  const handleTableChange = (tableName) => {
    setSelectedTable(tableName);
    setLoading(true);
  };

  const handleViewReport = () => {
    alert(`生成 ${selectedTable} 表的详细报告...`);
  };

  if (loading) {
    return (
      <div className="data-owner-container">
        <div className="loading-spinner">正在加载数据...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="data-owner-container">
        <div className="error-message">{error}</div>
      </div>
    );
  }

  return (
    <div className="data-owner-container">
      {/* 顶部导航栏 */}
      <div className="owner-header">
        <div className="header-left">
          <h1>CryptoDB</h1>
          <span className="role-badge">数据拥有者模式</span>
        </div>
        <button className="logout-btn" onClick={onLogout}>
          退出登录
        </button>
      </div>

      {/* 主内容区域 */}
      <div className="owner-content">
        {/* 左侧数据面板 - 更接近图片样式 */}
        <div className="data-panel">
          <div className="panel-header">
            <h2>YOUR DATA</h2>
            <div className="table-selector">
              <select 
                value={selectedTable} 
                onChange={(e) => handleTableChange(e.target.value)}
                className="table-dropdown"
              >
                <option value="user_credit">user_credit</option>
                <option value="user_profiles">user_profiles</option>
                <option value="transactions">transactions</option>
              </select>
            </div>
          </div>

          <div className="data-content">
            <div className="data-grid">
              <div className="data-row header-row">
                {getTableHeaders(selectedTable).map(header => (
                  <div key={header} className="data-cell header-cell">
                    {header.toUpperCase()}
                  </div>
                ))}
              </div>

              {tableData.slice(0, 5).map((row, index) => (
                <div key={index} className="data-row">
                  {Object.values(row).map((value, cellIndex) => (
                    <div key={cellIndex} className="data-cell">
                      {typeof value === 'number' ? value.toLocaleString() : value}
                    </div>
                  ))}
                </div>
              ))}
            </div>

            <div className="data-summary">
              <div className="summary-item">
                <label>记录总数:</label>
                <span>{tableData.length}</span>
              </div>
              <div className="summary-item">
                <label>最后同步:</label>
                <span>{new Date().toLocaleDateString()} 11:00:00</span>
              </div>
            </div>

            <button className="view-report-btn" onClick={handleViewReport}>
              VIEW DETAILED REPORT
            </button>
          </div>
        </div>

        {/* 右侧操作面板 - 保持图片样式 */}
        <div className="operations-panel">
          <div className="panel-header">
            <h2>CLOUD OPERATIONS</h2>
          </div>
          <div className="operations-content">
            <button className="upload-btn" onClick={handleUpload}>
              UPLOAD DATABASE TO CLOUD
            </button>
            <button className="settings-btn" onClick={handleSyncSettings}>
              SYNC SETTINGS
            </button>

            <div className="sync-status">
              <label>Sync Status:</label>
              <span className={`status-${syncStatus.toLowerCase()}`}>
                {syncStatus}
              </span>
            </div>
            <div className="progress-bar">
              <div 
                className="progress-fill"
                style={{ 
                  width: syncStatus === 'Uploading...' ? '70%' : 
                         syncStatus === 'Completed' ? '100%' : '0%' 
                }}
              ></div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default DataOwnerPanel;
