import React, { useState } from 'react';
import './CryptoDBPanel.css'; // 引入样式文件

function CryptoDBPanel() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState('');

//   const handleSecureQuery = () => {
//     if (!query.trim()) {
//       alert('Please enter a query before submitting.');
//       return;
//     }
//     // 模拟发送到后端（后续可替换为真实 fetch）
//     // fetch('/api/secureQuery', { method: 'POST', body: JSON.stringify({ data: query }) })
//     //   .then(...) 
//     setResults(`[模拟加密查询结果]\n你输入的查询是：${query}\n（这里会显示后端返回的加密结果）`);
//   };
  const handleSecureQuery = async () => {
  if (!query.trim()) {
    alert('请输入一个查询后再提交。');
    return;
  }

  try {
    // 发送 POST 请求到后端 API
    const response = await fetch('http://172.28.7.202:8080/api', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json', // 告诉后端我们传的是 JSON
      },
      body: JSON.stringify({ query }), // 发送一个 JSON 对象，包含用户输入的 query
    });

    if (!response.ok) {
      // 如果 HTTP 请求失败，比如 500 或 404
      const errorText = await response.text(); // 或 response.json()，根据后端返回格式
      throw new Error(`后端请求失败: ${response.status} - ${errorText}`);
    }

    // 读取后端返回的文本（或 JSON，根据实际情况调整）
    const resultFromBackend = await response.text(); // 如果后端返回纯文本
    // 如果后端返回的是 JSON，比如 { "result": "xxx" }，则使用：
    // const data = await response.json();
    // const resultFromBackend = data.result;

    // 更新页面上的结果框
    setResults(resultFromBackend);

  } catch (error) {
    console.error('请求出错：', error);
    setResults(`❌请求后端失败: ${error.message}`);
  }
};

  const handleClearInput = () => {
    setQuery('');
    setResults('');
  };

  const handleDecryptResults = () => {
    if (!results.trim()) {
      alert('No encrypted results to decrypt.');
      return;
    }
    // 模拟解密（实际应发送到后端）
    setResults(`[模拟解密结果]\n原始加密内容：\n${results}\n\n解密后：这是一个模拟的解密结果！`);
  };

  return (
    <div className="container">
      <div className="logo-container">
        <div className="logo"></div>
        <div className="logo-text">CryptoDB UI</div>
      </div>

      <div className="main-content">
        <div className="query-section">
          <div className="panel">
            <div className="panel-title">Encrypted Query Input</div>
            <textarea
              id="query-input"
              className="query-input"
              placeholder="Enter your encrypted SQL query here..."
              value={query}
              onChange={(e) => setQuery(e.target.value)}
            ></textarea>
            <button id="secure-query" className="btn btn-secure" onClick={handleSecureQuery}>
              Secure Query
            </button>
            <button id="clear-input" className="btn btn-clear" onClick={handleClearInput}>
              Clear Input
            </button>
          </div>

          <div className="panel">
            <div className="panel-title">Query Results (Encrypted)</div>
            <div id="query-results" className="query-results">
              {results}
            </div>
            <button id="decrypt-results" className="btn btn-decrypt" onClick={handleDecryptResults}>
              Decrypt Results
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default CryptoDBPanel;
