import React, {  useState, useEffect, useRef } from 'react';
import './CryptoDBPanel.css'; // ÂºïÂÖ•Ê†∑ÂºèÊñá‰ª∂

function CryptoDBPanel({ onLogout }) {
  const [query, setQuery] = useState('');
  const [decryptedOutput, setDecryptedOutput] = useState('');
  const [selectedTable, setSelectedTable] = useState('user_stats');
  const [statusMessages, setStatusMessages] = useState([]); // Êñ∞Â¢ûÁä∂ÊÄÅÔºåÁî®‰∫éÂ≠òÂÇ®Áä∂ÊÄÅÊ∂àÊÅØ
  const [selectedQueryMode, setSelectedQueryMode] = useState('exact'); // ÈªòËÆ§ÊòØÁ≠âÂÄºÊü•ËØ¢
  const [rowData0, setRowData0] = useState({}); // {columnName: [{ans0: value, rowId: id}, ...]}
  const [rowData1, setRowData1] = useState({});


  // ÂàõÂª∫ ref Áî®‰∫éÁä∂ÊÄÅËæìÂá∫ÂÆπÂô®
  const statusOutputRef = useRef(null);

  // Ê∑ªÂä†Áä∂ÊÄÅÊ∂àÊÅØÁöÑÂáΩÊï∞
  const addStatusMessage = (message) => {
    setStatusMessages(prev => [...prev, `> ${new Date().toLocaleTimeString()}: ${message}`]);
  };

  // Ê∏ÖÈô§Áä∂ÊÄÅÊ∂àÊÅØÁöÑÂáΩÊï∞
  const handleClearStatus = () => {
    setStatusMessages([]);
    addStatusMessage('Áä∂ÊÄÅÊó•ÂøóÂ∑≤Ê∏ÖÈô§');
  };

   // ÂΩìÁä∂ÊÄÅÊ∂àÊÅØÊõ¥Êñ∞Êó∂ÔºåËá™Âä®ÊªöÂä®Âà∞Â∫ïÈÉ®
  useEffect(() => {
    if (statusOutputRef.current) {
      statusOutputRef.current.scrollTop = statusOutputRef.current.scrollHeight;
    }
  }, [statusMessages]); // ÂΩì statusMessages ÂèòÂåñÊó∂Ëß¶Âèë

  const handleSecureQuery = async () => {
    if (!query.trim()) {
      alert('ËØ∑ËæìÂÖ•‰∏Ä‰∏™Ê≠£Êï¥Êï∞ÂêéÂÜçÊèê‰∫§„ÄÇ');
      return;
    }

    addStatusMessage('Ê≠£Âú®Êèê‰∫§ÂÆâÂÖ®Êü•ËØ¢...');

    try {
      const response = await fetch('http://172.28.7.202:8080/api/secureQuery', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ query, table: selectedTable }),
      });

      const rawData = await response.text();
      const data = JSON.parse(rawData);
      console.log('main:', data);

      addStatusMessage('Êü•ËØ¢Â∑≤ÊàêÂäüÊèê‰∫§Âà∞ÊúçÂä°Âô®');
      addStatusMessage('ËØ∑‰ªéÊúçÂä°Âô®Ëé∑ÂèñÁªìÊûú');
    } catch (error) {
      addStatusMessage(`Êü•ËØ¢Â§±Ë¥•: ${error.message}`);
    }
  };

  // ===== Êñ∞Â¢ûÔºö‰ªé Server1 (8081) ÂçïÁã¨Ëé∑Âèñ ans0 =====
const handleFetchAns0FromServer1 = async () => {
  addStatusMessage('Ê≠£Âú®‰ªé Server1 (8081) Ëé∑ÂèñË°åÁ∫ßÁªìÊûú...');

  try {
    const res = await fetch('http://172.28.7.202:8081/server0', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
    });

    const rawData = await res.text();
    const data = JSON.parse(rawData);

    console.log('server0 Data:', data.columns);

    if (data && data.columns) {
      const formattedData = {};

      data.columns.forEach(col => {
        // ÂÅáËÆæÊØèÂàóÁöÑ ans0 ÂÄºÊòØ‰∏Ä‰∏™Êï∞ÁªÑÔºåÊØè‰∏™ÂÖÉÁ¥†‰ª£Ë°®‰∏ÄË°å
        // Â¶ÇÊûúÂêéÁ´ØËøîÂõûÁöÑÊòØÂçï‰∏™ÂÄºÔºåÈúÄË¶ÅË∞ÉÊï¥‰∏∫Êï∞ÁªÑÂΩ¢Âºè
        const ans0Values = Array.isArray(col.ans0_values) ? col.ans0_values : [col.ans0];
        formattedData[col.name] = ans0Values.map((value, index) => ({
          ans0: value,
        }));
      });
      console.log('server0 formattedData:',formattedData);
      setRowData0(formattedData);
      addStatusMessage(`ÊàêÂäü‰ªé Server1 Ëé∑Âèñ ${Object.keys(formattedData).length} ÂàóÁöÑË°åÊï∞ÊçÆ`);
    }
  } catch (e) {
    addStatusMessage(`Ëé∑Âèñ Server1 Ë°åÊï∞ÊçÆÂ§±Ë¥•: ${e.message}`);
    console.error('Fetch from Server1 error:', e);
  }
};

  // ===== Êñ∞Â¢ûÔºö‰ªé Server2 (8082) ÂçïÁã¨Ëé∑Âèñ ans1 =====
  const handleFetchAns1FromServer2 = async () => {
  addStatusMessage('Ê≠£Âú®‰ªé Server2 (8082) Ëé∑ÂèñË°åÁ∫ßÁªìÊûú...');

  try {
    const res = await fetch('http://172.28.7.202:8082/server1', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
    });

    const rawData = await res.text();
    const data = JSON.parse(rawData);

    console.log('server1 Data:', data);
    if (data && data.columns) {
      const formattedData = {};

      data.columns.forEach(col => {
        // ÂÅáËÆæÊØèÂàóÁöÑ ans1 ÂÄºÊòØ‰∏Ä‰∏™Êï∞ÁªÑÔºåÊØè‰∏™ÂÖÉÁ¥†‰ª£Ë°®‰∏ÄË°å
        // Â¶ÇÊûúÂêéÁ´ØËøîÂõûÁöÑÊòØÂçï‰∏™ÂÄºÔºåÈúÄË¶ÅË∞ÉÊï¥‰∏∫Êï∞ÁªÑÂΩ¢Âºè
        const ans1Values = Array.isArray(col.ans1_values) ? col.ans1_values : [col.ans1];

        formattedData[col.name] = ans1Values.map((value, index) => ({
          ans1: value,
        }));
      });
      console.log('server1 formattedData:', formattedData);
      setRowData1(formattedData);
      addStatusMessage(`ÊàêÂäü‰ªé Server2 Ëé∑Âèñ ${Object.keys(formattedData).length} ÂàóÁöÑË°åÊï∞ÊçÆ`);
    }
  } catch (e) {
    addStatusMessage(`Ëé∑Âèñ Server2 Ë°åÊï∞ÊçÆÂ§±Ë¥•: ${e.message}`);
    console.error('Fetch from Server2 error:', e);
  }
};

  const handleClearInput = () => {
    setQuery('');
    setDecryptedOutput('');
    addStatusMessage('Â∑≤Ê∏ÖÈô§ÊâÄÊúâËæìÂÖ•ÂíåÁªìÊûú');
    setRowData0({});
    setRowData1({});
  };

const handleDecryptResults = () => {
  addStatusMessage('Ê≠£Âú®Ëß£ÂØÜÁªìÊûú...');

  if (Object.keys(rowData0).length === 0 || Object.keys(rowData1).length === 0) {
    setDecryptedOutput('No data yet, please query Server1 and Server2 first');
    addStatusMessage('Ëß£ÂØÜÂ§±Ë¥•ÔºöÁº∫Â∞ë Server1 Êàñ Server2 Êï∞ÊçÆ');
    return;
  }

  // ÊûÑÂª∫‰∏Ä‰∏™Êñ∞ÁöÑÂØπË±°ÔºödecryptedOutput
  const decryptedOutput = {};

  // ÈÅçÂéÜ rowData0 ÁöÑÊØè‰∏Ä‰∏™Â≠óÊÆµÔºàÂ¶Ç ID, is_active, order_amountÔºâ
  Object.keys(rowData0).forEach((key) => {
    const arr0 = rowData0[key]; // ÊØîÂ¶Ç [1, 2, 3, ...]
    const arr1 = rowData1[key] || []; // Èò≤Ê≠¢ key Âú® rowData1 ‰∏≠‰∏çÂ≠òÂú®

    // ÊûÑÂª∫Â∑ÆÂÄºÊï∞ÁªÑ
    const diffArray = arr0.map((val0, i) => {
      const val1 = arr1[i]; // ÂèØËÉΩ‰∏∫ undefined

      // Á°Æ‰øù‰∏§ËÄÖÈÉΩÊòØÊï∞Â≠ó
      const num0 = Number(val0);
      const num1 = Number(val1);

      if (isNaN(num0) || isNaN(num1)) {
        return 'N/A'; // ÊàñËÄÖ‰Ω†ÂèØ‰ª•ËøîÂõû null / '-' / 0ÔºåÊ†πÊçÆÈúÄÊ±Ç
      }

      return num0 - num1; // ËøîÂõûÊï∞Â≠óÂ∑ÆÂÄº
    });

    // Â≠òÂÖ•ÁªìÊûúÂØπË±°
    decryptedOutput[key] = diffArray;
  });

  // ‚úÖ Áé∞Âú® decryptedOutput Â∞±ÊòØÔºö
  // {
  //   ID: [Â∑Æ, Â∑Æ, Â∑Æ...],
  //   is_active: [Â∑Æ, Â∑Æ...],
  //   order_amount: [Â∑Æ, Â∑Æ...]
  // }

  console.log('Decrypted Output (Â∑ÆÂÄºÊï∞ÁªÑ):', decryptedOutput);

  // Â¶ÇÊûú‰Ω†Â∏åÊúõ‰ª•ÊüêÁßçÂΩ¢ÂºèÂ±ïÁ§∫Ëøô‰∏™ decryptedOutputÔºåÊØîÂ¶ÇËÆæÁΩÆÂà∞ state Âπ∂Ê∏≤ÊüìË°®Ê†ºÔºåÂèØ‰ª•ÁªßÁª≠Ôºö
  setDecryptedOutput(decryptedOutput); // Â¶ÇÊûú‰Ω†‰πãÂêéË¶ÅÁî®ÂÆÉÊ∏≤ÊüìË°®Ê†º

  addStatusMessage('Ëß£ÂØÜÂÆåÊàêÔºöÂ∑≤ÁîüÊàêÂêÑÂàóÂ∑ÆÂÄºÊï∞ÁªÑ');
};

  return (
    <div className="container">

      <div className="logo-container">
        <div className="logo"></div>
        <div className="logo-text">CryptoDB</div>
         <button className="logout-btn" onClick={onLogout}>
          ÈÄÄÂá∫ÁôªÂΩï
        </button>

      </div>

    <span className="role-badge">Êü•ËØ¢ËÄÖÊ®°Âºè</span>
      <div className="main-content">

        <div className="query-section">
          <div className="panel">
            <div className="panel-title">Ê£ÄÁ¥¢Ê®°Âºè</div>

            {/* üîß Êñ∞Â¢ûÔºöÊü•ËØ¢Ê®°Âºè label + ‰∏ãÊãâËèúÂçï */}

            <select
              id="query-mode-select"
              className="table-select query-mode-select"  // Â§çÁî®ÊàñÊñ∞Â¢ûÊ†∑Âºè
              value={selectedQueryMode}
              onChange={(e) => setSelectedQueryMode(e.target.value)}
            >
              <option value="exact">Á≠âÂÄºÊü•ËØ¢</option>
              <option value="range">ËåÉÂõ¥Êü•ËØ¢</option>
              <option value="prefix">ÂâçÁºÄÊü•ËØ¢</option>
            </select> 
            <textarea
              id="query-input"
              className="query-input"
              placeholder="Enter your encrypted SQL query here..."
              value={query}
              onChange={(e) => setQuery(e.target.value)}
            ></textarea>
            <label htmlFor="query-mode-select" className="query-mode-label">ÈÄâÊã©Ë°®</label>
            <select
              id="table-select"
              className="table-select"
              value={selectedTable}
              onChange={(e) => setSelectedTable(e.target.value)}
            >

              <option value="user_stats">user_stats</option>
              <option value="user_credit">user_credit</option>             
            </select>          

            <button id="secure-query" className="btn btn-secure" onClick={handleSecureQuery}>
              Secure Query
            </button>
            <button id="clear-input" className="btn btn-clear" onClick={handleClearInput}>
              Clear Input
            </button>

            {/* Êñ∞Â¢ûÔºöÁä∂ÊÄÅÊòæÁ§∫Ê°Ü */}
            <div className="status-panel">
              <div className="status-header">
                <div className="status-title">Query Status</div>
                <button className="btn btn-clear-status" onClick={handleClearStatus}>
                  Clear Status
                </button>
              </div>
              <div className="status-output" ref={statusOutputRef}>
                {statusMessages.map((msg, index) => (
                  <div key={index} className="status-message">{msg}</div>
                ))}
              </div>
            </div>
          </div>

          <div className="panel">
            <div className="panel-title">Query Results S1 & S2(Encrypted)</div>


  {/* Server1 Ë°®Ê†º */}

               <div className="results1-container">
              <div className="result1-item">
                IPÂú∞ÂùÄ:172.28.7.202 Á´ØÂè£Âè∑:8081
              </div>
              <button
                className="btn-small"
                onClick={handleFetchAns0FromServer1}
              >
                <div className="result2-item">
                  Get result from S1
                </div>
              </button>
            </div>

  {/* Server1 Ë°®Ê†º */}
  <div className="query-results-left">


    {Object.keys(rowData0).length === 0 ? (
      <div className="result-item">No data from Server1</div>
    ) : (
     <div className="table-scroll">
    <table className="data-table">
         <thead>
    <tr>
      {Object.keys(rowData0).map(colName => (
        <th key={`s1-header-${colName}`} className="data-header">
          {colName}
        </th>
      ))}
    </tr>
  </thead>
          <tbody>
            {(() => {
              // Ëé∑ÂèñÊúÄÂ§ßË°åÊï∞
              const maxRows = Math.max(
                ...Object.values(rowData0).map(col => col.length)
              );

              // ÁîüÊàêË°åÊï∞ÊçÆ
              const rows = [];
              for (let i = 0; i < maxRows; i++) {
                rows.push(
                  <tr key={`s1-row-${i}` } className="data-body-row1">

                    {Object.keys(rowData0).map(colName => (
                      <td key={`s1-cell-${colName}-${i}`} className="data-body-cell1">
                        {rowData0[colName][i]?.ans0 || '-'}
                      </td>
                    ))}
                  </tr>
                );
              }
              return rows;
            })()}
          </tbody>
        </table>
      </div>
    )}
  </div>


               <div className="results1-container">
              <div className="result1-item">
                IPÂú∞ÂùÄ:172.28.7.202 Á´ØÂè£Âè∑:8082
              </div>
              <button
                className="btn-small"
                onClick={handleFetchAns1FromServer2}
              >
                <div className="result2-item">
                  Get result from S2
                </div>
              </button>
            </div>

  {/* Server2 Ë°®Ê†º */}
  <div className="query-results-right">
    {Object.keys(rowData1).length === 0 ? (
      <div className="result-item">No data from Server2</div>
    ) : (
      <div className="table-scroll">
    <table className="data-table">
          <thead>
    <tr>
      {Object.keys(rowData0).map(colName => (
        <th key={`s1-header-${colName}`} className="data-header">
          {colName}
        </th>
      ))}
    </tr>
  </thead>
          <tbody>
            {(() => {
              // Ëé∑ÂèñÊúÄÂ§ßË°åÊï∞
              const maxRows = Math.max(
                ...Object.values(rowData1).map(col => col.length)
              );

              // ÁîüÊàêË°åÊï∞ÊçÆ
              const rows = [];
              for (let i = 0; i < maxRows; i++) {
                rows.push(
                  <tr key={`s2-row-${i}`} className="data-body-row1">

                    {Object.keys(rowData1).map(colName => (
                      <td key={`s2-cell-${colName}-${i}`} className="data-body-cell1">
                        {rowData1[colName][i]?.ans1 || '-'}
                      </td>
                    ))}
                  </tr>
                );
              }
              return rows;
            })()}
          </tbody>
        </table>
      </div>
    )}
  </div>


            <button id="decrypt-results" className="btn btn-decrypt" onClick={handleDecryptResults}>
              Decrypt Results
            </button>

            <div className="query-results">
              {decryptedOutput}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default CryptoDBPanel;
