import React, { useState } from 'react';
import './LoginPage.css';




function LoginPage({ onLoginSuccess }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showSuccessModal, setShowSuccessModal] = useState(false); // ÊéßÂà∂ÊòØÂê¶ÊòæÁ§∫ÊàêÂäüÂºπÁ™ó

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!username.trim() || !password.trim()) {
      alert('ËØ∑ËæìÂÖ•Áî®Êà∑ÂêçÂíåÂØÜÁ†Å');
      return;
    }

    // Ê®°ÊãüÁôªÂΩïÈ™åËØÅÔºàÊÇ®ÂèØ‰ª•ÊõøÊç¢‰∏∫ÁúüÂÆû API ËØ∑Ê±ÇÔºâ
    // ÂÅáËÆæÁî®Êà∑ÂêçÊòØ adminÔºåÂØÜÁ†ÅÊòØ 123456
    if (username === 'admin' && password === '123456') {
        setShowSuccessModal(true); // ÊòæÁ§∫Ëá™ÂÆö‰πâÊàêÂäüÂºπÁ™ó
      // ÈÄöÁü•Áà∂ÁªÑ‰ª∂ÔºàApp.jsÔºâÁôªÂΩïÊàêÂäüÔºåËøõÂÖ•‰∏ªÁïåÈù¢
        setTimeout(() => {
            setShowSuccessModal(false);
            if (onLoginSuccess) {
                onLoginSuccess(); // ÈÄöÁü•ÁôªÂΩïÊàêÂäüÔºåËøõÂÖ•‰∏ªÁïåÈù¢
            }
        }, 5000);

      if (onLoginSuccess) {
        onLoginSuccess(); // ÂàáÊç¢Âà∞‰∏ªÈ°µÈù¢
      }
    } else {
      alert('Áî®Êà∑ÂêçÊàñÂØÜÁ†ÅÈîôËØØÔºÅ');
    }
  };

  const handleModalConfirm = () => {
    setShowSuccessModal(false);
    if (onLoginSuccess) {
      onLoginSuccess(); // ÈÄöÁü• App.jsÔºåÁôªÂΩïÊàêÂäüÔºåË∑≥ËΩ¨‰∏ªÈ°µ
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>üîê Áî®Êà∑ÁôªÂΩï</h2>
        <form onSubmit={handleSubmit}>
          <div className="input-group">
            <label htmlFor="username">Áî®Êà∑Âêç</label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="ËØ∑ËæìÂÖ•Áî®Êà∑Âêç"
              required
            />
          </div>
          <div className="input-group">
            <label htmlFor="password">ÂØÜÁ†Å</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="ËØ∑ËæìÂÖ•ÂØÜÁ†Å"
              required
            />
          </div>
          <button type="submit" className="login-btn">
            ÁôªÂΩï
          </button>
        </form>
      </div>
    </div>
  );
}

export default LoginPage;
