import React, { useState, useEffect } from 'react';
import './DataOwnerPanel.css';



function DataOwnerPanel({ onLogout }) {
  const [tables, setTables] = useState({});
  const [selectedTable, setSelectedTable] = useState('');
  const [tableData, setTableData] = useState([]);
  const [tableHeaders, setTableHeaders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [syncStatus, setSyncStatus] = useState('Idle');

  // ‰ªéÊúçÂä°Âô®Âä†ËΩΩ SQL Êñá‰ª∂
  // ‰ªé public/sql/ Âä†ËΩΩ SQL Êñá‰ª∂ÔºåÊØîÂ¶Ç data_init.sql
const loadSQLFile = async () => {
  try {
    setLoading(true);
    const response = await fetch('/sql/data_init.sql'); // ‚úÖ ‰Ω†ÁöÑÁõÆÊ†á SQL Êñá‰ª∂
    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }
    const sqlContent = await response.text();
    return sqlContent;
  } catch (err) {
    setError(`Êó†Ê≥ïÂä†ËΩΩ SQL Êñá‰ª∂: ${err.message}`);
    throw err;
  }
};

  // Ëß£Êûê SQL Êñá‰ª∂ÂÜÖÂÆπÔºåËøîÂõû { Ë°®Âêç: { columns: [], data: [] } }
const parseSQLFile = (sqlContent) => {
  const tables = {};  // { Ë°®Âêç: { columns: [], data: [] } }
  const lines = sqlContent.split('\n');
  let currentTable = null;

  for (const line of lines) {
    const trimmedLine = line.trim();

    // Ë∑≥ËøáÁ©∫Ë°å„ÄÅÊ≥®Èáä„ÄÅLOCK/UNLOCK/USE/DROP
    if (!trimmedLine ||
        trimmedLine.startsWith('--') ||
        trimmedLine.startsWith('#') ||
        trimmedLine.startsWith('LOCK TABLES') ||
        trimmedLine.startsWith('UNLOCK TABLES') ||
        trimmedLine.startsWith('USE ') ||
        trimmedLine.startsWith('DROP ') ||
        trimmedLine.startsWith('SET ') ||
        trimmedLine.startsWith('ENGINE=') ||
        trimmedLine.startsWith('DEFAULT CHARSET=')) {
      continue;
    }

    // üü¢ ÂåπÈÖç CREATE TABLE ËØ≠Âè•ÔºåËé∑ÂèñË°®Âêç
    const createTableMatch = trimmedLine.match(/CREATE TABLE (?:IF NOT EXISTS )?[`"]?(\w+)[`"]?/i);
    if (createTableMatch) {
      currentTable = createTableMatch[1];
      tables[currentTable] = {
        columns: [],
        data: []
      };
      continue;
    }

    // üü¢ ÂåπÈÖçÂàóÂÆö‰πâË°åÔºåÂ¶Ç `ID` varchar(64) NOT NULL,
    if (currentTable && trimmedLine.match(/^[`"]?\w+[`"]?\s+\w+/)) {
      const columnMatch = trimmedLine.match(/^[`"]?(\w+)[`"]?\s+(\w+)/);
      if (columnMatch) {
        const colName = columnMatch[1];
        const colType = columnMatch[2];
        // Âè™Ë¶Å‰∏çÊòØ‰∏ªÈîÆÊàñ KEY Áõ∏ÂÖ≥ÁöÑÂ≠óÊÆµÂÆö‰πâÔºåÂ∞±ËÆ§‰∏∫ÊòØÊôÆÈÄöÂàó
        if (!trimmedLine.includes('PRIMARY KEY') && !trimmedLine.includes('KEY') && !trimmedLine.includes('UNIQUE')) {
          tables[currentTable].columns.push({
            name: colName,
            type: colType
          });
        }
      }
      continue;
    }

    // üü¢ ÂåπÈÖç INSERT INTO ËØ≠Âè•ÔºåÂ¶Ç INSERT INTO user_credit VALUES (...);
    const insertMatch = trimmedLine.match(/INSERT INTO (?:IGNORE )?[`"]?(\w+)[`"]?\s*(?:\s\([^)]+\))?\s+VALUES\s*\(([^)]+)\)/i);
    if (insertMatch) {
      const tableName = insertMatch[1];
      const valuesStr = insertMatch[2];

      if (!tables[tableName]) {
        // Â¶ÇÊûúË°®ËøòÊú™ÂÆö‰πâÔºå‰πüÂÖÅËÆ∏Ôºà‰ΩÜÊúÄÂ•ΩÊúâ CREATE TABLEÔºâ
        tables[tableName] = { columns: [], data: [] };
      }

      // Ëß£Êûê VALUES ÈáåÁöÑÊØè‰∏™ÂÄºÔºåÂéªÈô§ÂºïÂè∑ÔºåËΩ¨‰∏∫Êï∞Â≠óÊàñÂ≠óÁ¨¶‰∏≤
      const values = valuesStr
        .split(',')
        .map(v => v.trim())
        .map(v => {
          // ÂéªÊéâÂ≠óÁ¨¶‰∏≤‰∏§Á´ØÁöÑÂºïÂè∑ÔºåÂåÖÊã¨ÂçïÂºïÂè∑„ÄÅÂèåÂºïÂè∑„ÄÅÂèçÂºïÂè∑
          let cleaned = v.replace(/^['"`]|['"`]$/g, '');
          // Â∞ùËØïËΩ¨‰∏∫Êï∞Â≠ó
          if (!isNaN(cleaned) && cleaned !== '') {
            return Number(cleaned);
          }
          return cleaned; // Âê¶Âàô‰øùÁïô‰∏∫Â≠óÁ¨¶‰∏≤
        });

      if (values.length > 0) {
        tables[tableName].data.push(values);
      }
      continue;
    }
  }

  return tables;
};
