import React, { useState, useEffect } from 'react';
import './DataOwnerPanel.css';



function DataOwnerPanel({ onLogout }) {
  const [tables, setTables] = useState({});
  const [selectedTable, setSelectedTable] = useState('');
  const [tableData, setTableData] = useState([]);
  const [tableHeaders, setTableHeaders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [syncStatus, setSyncStatus] = useState('Idle');

  // 从服务器加载 SQL 文件
  const loadSQLFile = async () => {
    try {
      setLoading(true);
      const response = await fetch('/sql/21.sql');
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const sqlContent = await response.text();
      return sqlContent;
    } catch (err) {
      setError(`无法加载 SQL 文件: ${err.message}`);
      throw err;
    }
  };

  // 解析 SQL 文件
  const parseSQLFile = (sqlContent) => {
    const tables = {};
    const lines = sqlContent.split('\n');
    let currentTable = null;
    let inInsertMode = false;

    for (const line of lines) {
      const trimmedLine = line.trim();

      // 跳过空行和注释
      if (!trimmedLine || trimmedLine.startsWith('--') || trimmedLine.startsWith('#')) {
        continue;
      }

      // 检测 CREATE TABLE 语句
      const createTableMatch = trimmedLine.match(/CREATE TABLE (?:IF NOT EXISTS )?[`"]?(\w+)[`"]?/i);
      if (createTableMatch) {
        currentTable = createTableMatch[1];
        tables[currentTable] = {
          columns: [],
          data: []
        };
        continue;
      }

      // 检测列定义（更健壮的匹配）
      if (currentTable && trimmedLine.match(/^[`"]?\w+[`"]?\s+\w+/)) {
        const columnMatch = trimmedLine.match(/^[`"]?(\w+)[`"]?\s+(\w+)/);
        if (columnMatch && !trimmedLine.includes('PRIMARY KEY') && !trimmedLine.includes('KEY')) {
          tables[currentTable].columns.push({
            name: columnMatch[1],
            type: columnMatch[2]
          });
        }
        continue;
      }

      // 检测 INSERT 语句
      const insertMatch = trimmedLine.match(/INSERT INTO (?:IGNORE )?[`"]?(\w+)[`"]?\s*(?:\s\([^)]+\))?\s+VALUES\s*\(([^)]+)\)/i);
      if (insertMatch) {
        const tableName = insertMatch[1];
        const valuesStr = insertMatch[2];

        // 解析 VALUES 中的值（处理各种引号格式）
        const values = valuesStr.split(',').map(val => {
          let trimmedVal = val.trim();
          // 去除各种引号
          trimmedVal = trimmedVal.replace(/^['"`]|['"`]$/g, '');
          // 尝试转换为数字
          if (!isNaN(trimmedVal) && trimmedVal !== '') {
            return Number(trimmedVal);
          }
          return trimmedVal;
        });

        if (tables[tableName]) {
          tables[tableName].data.push(values);
        }
        continue;
      }

      // 检测 LOCK/UNLOCK 语句（跳过）
      if (trimmedLine.startsWith('LOCK TABLES') || trimmedLine.startsWith('UNLOCK TABLES')) {
        continue;
      }

      // 检测 USE 语句（跳过）
      if (trimmedLine.startsWith('USE ')) {
        continue;
      }

      // 检测 DROP 语句（跳过）
      if (trimmedLine.startsWith('DROP ')) {
        continue;
      }
    }

    return tables;
  };

  // 格式化数据为对象数组
  const formatTableData = (tableInfo) => {
    if (!tableInfo || !tableInfo.columns || !tableInfo.data) return [];

    return tableInfo.data.map(row => {
      const obj = {};
      tableInfo.columns.forEach((column, index) => {
        obj[column.name] = row[index] || '';
      });
      return obj;
    });
  };

  useEffect(() => {
    const initializeDatabase = async () => {
      try {
        // 从服务器加载 SQL 文件
        const sqlContent = await loadSQLFile();

        // 解析 SQL 文件
        const parsedTables = parseSQLFile(sqlContent);
        setTables(parsedTables);

        // 设置默认选中的表
        const tableNames = Object.keys(parsedTables);
        if (tableNames.length > 0) {
          setSelectedTable(tableNames[0]);
          const tableInfo = parsedTables[tableNames[0]];
          setTableData(formatTableData(tableInfo));
          setTableHeaders(tableInfo.columns.map(col => col.name));
        }

        setLoading(false);
      } catch (err) {
        console.error('初始化数据库失败:', err);
        setLoading(false);
        // 可以设置回退到模拟数据
      }
    };

    initializeDatabase();
  }, []);

  // ... 其余代码保持不变 ...

  const handleTableChange = (tableName) => {
    setSelectedTable(tableName);
    const tableInfo = tables[tableName];
    if (tableInfo) {
      setTableData(formatTableData(tableInfo));
      setTableHeaders(tableInfo.columns.map(col => col.name));
    }
  };

  const handleUpload = () => {
    setSyncStatus('Uploading...');
    setTimeout(() => {
      setSyncStatus('Completed');
      setTimeout(() => setSyncStatus('Idle'), 2000);
    }, 3000);
  };

  const handleSyncSettings = () => {
    alert('打开同步设置...');
  };

  const handleViewReport = () => {
    alert(`生成 ${selectedTable} 表的详细报告...`);
  };

  if (loading) {
    return (
      <div className="data-owner-container">
        <div className="loading-spinner">正在解析数据库结构...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="data-owner-container">
        <div className="error-message">{error}</div>
      </div>
    );
  }

  return (
    <div className="data-owner-container">
      <div className="owner-header">
        <div className="header-left">
          <h1>CryptoDB</h1>
          <span className="role-badge">数据拥有者模式</span>
        </div>
        <button className="logout-btn" onClick={onLogout}>
          退出登录
        </button>
      </div>

      <div className="owner-content">
        <div className="data-panel">
          <div className="panel-header">
            <h2>YOUR DATA</h2>
            <div className="table-selector">
              <select 
                value={selectedTable} 
                onChange={(e) => handleTableChange(e.target.value)}
                className="table-dropdown"
              >
                {Object.keys(tables).map(tableName => (
                  <option key={tableName} value={tableName}>
                    {tableName}
                  </option>
                ))}
              </select>
            </div>
          </div>

          <div className="data-content">
            <div className="data-grid">
              <div className="data-row header-row">
                {tableHeaders.map(header => (
                  <div key={header} className="data-cell header-cell">
                    {header.toUpperCase()}
                  </div>
                ))}
              </div>

              {tableData.slice(0, 5).map((row, index) => (
                <div key={index} className="data-row">
                  {tableHeaders.map(header => (
                    <div key={header} className="data-cell">
                      {row[header]}
                    </div>
                  ))}
                </div>
              ))}
            </div>

            <div className="data-summary">
              <div className="summary-item">
                <label>表名:</label>
                <span>{selectedTable}</span>
              </div>
              <div className="summary-item">
                <label>记录数:</label>
                <span>{tableData.length}</span>
              </div>
              <div className="summary-item">
                <label>列数:</label>
                <span>{tableHeaders.length}</span>
              </div>
            </div>

            <button className="view-report-btn" onClick={handleViewReport}>
              VIEW DETAILED REPORT
            </button>
          </div>
        </div>

        <div className="operations-panel">
          <div className="panel-header">
            <h2>CLOUD OPERATIONS</h2>
          </div>
          <div className="operations-content">
            <button className="upload-btn" onClick={handleUpload}>
              UPLOAD DATABASE TO CLOUD
            </button>
            <button className="settings-btn" onClick={handleSyncSettings}>
              SYNC SETTINGS
            </button>

            <div className="database-info">
              <h3>数据库信息</h3>
              <div className="info-item">
                <label>表数量:</label>
                <span>{Object.keys(tables).length}</span>
              </div>
              <div className="info-item">
                <label>总记录:</label>
                <span>{Object.values(tables).reduce((sum, table) => sum + table.data.length, 0)}</span>
              </div>
            </div>

            <div className="sync-status">
              <label>Sync Status:</label>
              <span className={`status-${syncStatus.toLowerCase()}`}>
                {syncStatus}
              </span>
            </div>
            <div className="progress-bar">
              <div 
                className="progress-fill"
                style={{ 
                  width: syncStatus === 'Uploading...' ? '70%' : 
                         syncStatus === 'Completed' ? '100%' : '0%' 
                }}
              ></div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default DataOwnerPanel;
