import React, { useState } from 'react';
import './CryptoDBPanel.css'; // 引入样式文件

function CryptoDBPanel() {
  const [query, setQuery] = useState('');
  const [decryptedOutput, setDecryptedOutput] = useState('');
  const [ans0Value, setAns0Value] = useState('');
  const [ans1Value, setAns1Value] = useState('');
  const [ans0Display, setAns0Display] = useState('');
  const [ans1Display, setAns1Display] = useState('');
  const [selectedTable, setSelectedTable] = useState('user_credit'); // 默认选中 user_credit

  <select
  value={selectedTable}
  onChange={(e) => setSelectedTable(e.target.value)}
  className="table-select"
>
  <option value="user_credit">user_credit</option>
  <option value="financial_data">financial_data</option>
  <option value="user_behavior">user_behavior</option>
</select>


  const handleSecureQuery = async () => {
  if (!query.trim()) {
    alert('请输入一个正整数后再提交。');
    return;
  }

  const response = await fetch('http://172.28.7.202:8080/api/secureQuery', {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({ query }),
  });

  const rawData = await response.text();
  try {
    const data = JSON.parse(rawData);
    if (data.status === 'success') {
      setAns0Display(`ID: ${data.ans0}`);
      setAns1Display(`ID: ${data.ans1}`);
      setAns0Value(`${data.ans0}`);
      setAns1Value(`${data.ans1}`);
    } else {
      setAns0Value('❌ 错误: ' + (data.error || '未知'));
      setAns1Value('');
    }
  } catch (e) {
    setAns0Value('❌ 解析失败');
    setAns1Value('');
  }
};

  const handleClearInput = () => {
    setQuery('');
    setAns0Value('');
    setAns1Value('');
    setAns0Display('');
    setAns1Display('');
    setDecryptedOutput('');
  };

const handleDecryptResults = () => {


  // ✅ 更新新的状态，用于下方区域显示
  setDecryptedOutput(`ID:${ans0Value-ans1Value}`);
};

  return (
    <div className="container">
      <div className="logo-container">
        <div className="logo"></div>
        <div className="logo-text">CryptoDB UI</div>
      </div>

      <div className="main-content">
        <div className="query-section">
          <div className="panel">
            <div className="panel-title">Encrypted Query Input</div>
            <textarea
              id="query-input"
              className="query-input"
              placeholder="Enter your encrypted SQL query here..."
              value={query}
              onChange={(e) => setQuery(e.target.value)}
            ></textarea>
            <button id="secure-query" className="btn btn-secure" onClick={handleSecureQuery}>
              Secure Query
            </button>
            <button id="clear-input" className="btn btn-clear" onClick={handleClearInput}>
              Clear Input
            </button>
          </div>

          <div className="panel">
            <div className="panel-title">Query Results S1 & S2(Encrypted)</div>
             <div className="results-container">
              <div className="query-results">{ans0Display}</div>


            <div className="query-results">{ans1Display}  </div>

          </div>

          <button id="decrypt-results" className="btn btn-decrypt" onClick={handleDecryptResults}>
              Decrypt Results
            </button>
              <div className="query-results">
        {decryptedOutput} {/* 点击 Decrypt 后显示这里 */}
          </div>
        </div>
      </div>
    </div>
    </div>
  );
}

export default CryptoDBPanel;
