import React, { useState, useEffect } from 'react';
import './DataOwnerPanel.css';

function DataOwnerPanel({ onLogout }) {
  const [tables, setTables] = useState({});
  const [selectedTable, setSelectedTable] = useState('');
  const [tableData, setTableData] = useState([]);
  const [tableHeaders, setTableHeaders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [syncStatus, setSyncStatus] = useState('Idle');

  // 模拟数据库表数据（回退数据）
  const mockDatabaseTables = {
    user_credit: [
      { ID: "1", credit_rank: 6, income: 100000, age: 20 },
      { ID: "2", credit_rank: 5, income: 90000, age: 19 },
      { ID: "3", credit_rank: 6, income: 89700, age: 32 },
      { ID: "4", credit_rank: 6, income: 607000, age: 30 },
      { ID: "5", credit_rank: 5, income: 30070, age: 25 },
      { ID: "6", credit_rank: 6, income: 12070, age: 28 },
      { ID: "7", credit_rank: 6, income: 200800, age: 50 },
      { ID: "8", credit_rank: 6, income: 607000, age: 30 },
      { ID: "9", credit_rank: 5, income: 30070, age: 25 },
      { ID: "10", credit_rank: 5, income: 12070, age: 28 }
    ],
    user_profiles: [
      { user_id: "U001", username: "john_doe", email: "john@example.com", created_at: "2024-01-15 10:00:00", status: "active" },
      { user_id: "U002", username: "jane_smith", email: "jane@example.com", created_at: "2024-01-16 11:30:00", status: "active" },
      { user_id: "U003", username: "bob_wilson", email: "bob@example.com", created_at: "2024-01-17 09:15:00", status: "inactive" }
    ],
    transactions: [
      { transaction_id: 1, user_id: "U001", amount: 1000.00, type: "deposit", transaction_date: "2024-01-15 14:30:00", status: "completed" },
      { transaction_id: 2, user_id: "U001", amount: 250.50, type: "withdrawal", transaction_date: "2024-01-16 09:45:00", status: "completed" },
      { transaction_id: 3, user_id: "U002", amount: 3000.00, type: "transfer", transaction_date: "2024-01-17 16:20:00", status: "pending" }
    ]
  };

  // 从服务器加载 SQL 文件
  const loadSQLFile = async () => {
    try {
      const response = await fetch('/sql/data_init.sql');
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const sqlContent = await response.text();
      return sqlContent;
    } catch (err) {
      console.warn('无法从服务器加载 SQL 文件，使用模拟数据:', err.message);
      // 返回模拟的 SQL 内容作为回退
      return `
        CREATE TABLE user_credit (
          ID varchar(64) NOT NULL,
          credit_rank int NOT NULL,
          income int NOT NULL,
          age int NOT NULL,
          PRIMARY KEY (ID)
        );

        INSERT INTO user_credit VALUES ("1", 6, 100000, 20);
        INSERT INTO user_credit VALUES ("2", 5, 90000, 19);
        INSERT INTO user_credit VALUES ("3", 6, 89700, 32);
        INSERT INTO user_credit VALUES ("4", 6, 607000, 30);
        INSERT INTO user_credit VALUES ("5", 5, 30070, 25);
        INSERT INTO user_credit VALUES ("6", 6, 12070, 28);
        INSERT INTO user_credit VALUES ("7", 6, 200800, 50);
        INSERT INTO user_credit VALUES ("8", 6, 607000, 30);
        INSERT INTO user_credit VALUES ("9", 5, 30070, 25);
        INSERT INTO user_credit VALUES ("10", 5, 12070, 28);

        CREATE TABLE user_profiles (
          user_id varchar(64) NOT NULL,
          username varchar(100) NOT NULL,
          email varchar(150) NOT NULL,
          created_at datetime DEFAULT CURRENT_TIMESTAMP,
          status enum('active','inactive') DEFAULT 'active',
          PRIMARY KEY (user_id)
        );

        INSERT INTO user_profiles VALUES ("U001", "john_doe", "john@example.com", "2024-01-15 10:00:00", "active");
        INSERT INTO user_profiles VALUES ("U002", "jane_smith", "jane@example.com", "2024-01-16 11:30:00", "active");
        INSERT INTO user_profiles VALUES ("U003", "bob_wilson", "bob@example.com", "2024-01-17 09:15:00", "inactive");

        CREATE TABLE transactions (
          transaction_id int NOT NULL AUTO_INCREMENT,
          user_id varchar(64) NOT NULL,
          amount decimal(10,2) NOT NULL,
          type enum('deposit','withdrawal','transfer') NOT NULL,
          transaction_date datetime NOT NULL,
          status enum('pending','completed','failed') DEFAULT 'pending',
          PRIMARY KEY (transaction_id)
        );

        INSERT INTO transactions VALUES (1, "U001", 1000.00, "deposit", "2024-01-15 14:30:00", "completed");
        INSERT INTO transactions VALUES (2, "U001", 250.50, "withdrawal", "2024-01-16 09:45:00", "completed");
        INSERT INTO transactions VALUES (3, "U002", 3000.00, "transfer", "2024-01-17 16:20:00", "pending");
      `;
    }
  };

  // 解析 SQL 文件
  const parseSQLFile = (sqlContent) => {
    const tables = {};
    const lines = sqlContent.split('\n');
    let currentTable = null;

    for (const line of lines) {
      const trimmedLine = line.trim();

      // 跳过空行和注释
      if (!trimmedLine || trimmedLine.startsWith('--') || trimmedLine.startsWith('#')) {
        continue;
      }

      // 检测 CREATE TABLE 语句
      const createTableMatch = trimmedLine.match(/CREATE TABLE (?:IF NOT EXISTS )?[`"]?(\w+)[`"]?/i);
      if (createTableMatch) {
        currentTable = createTableMatch[1];
        tables[currentTable] = {
          columns: [],
          data: []
        };
        continue;
      }

      // 检测列定义
      if (currentTable && trimmedLine.match(/^[`"]?\w+[`"]?\s+\w+/)) {
        const columnMatch = trimmedLine.match(/^[`"]?(\w+)[`"]?\s+(\w+)/);
        if (columnMatch && !trimmedLine.includes('PRIMARY KEY') && !trimmedLine.includes('KEY')) {
          tables[currentTable].columns.push({
            name: columnMatch[1],
            type: columnMatch[2]
          });
        }
        continue;
      }

      // 检测 INSERT 语句
      const insertMatch = trimmedLine.match(/INSERT INTO (?:IGNORE )?[`"]?(\w+)[`"]?\s*(?:\s\([^)]+\))?\s+VALUES\s*\(([^)]+)\)/i);
      if (insertMatch) {
        const tableName = insertMatch[1];
        const valuesStr = insertMatch[2];

        // 解析 VALUES 中的值
        const values = valuesStr.split(',').map(val => {
          let trimmedVal = val.trim();
          // 去除各种引号
          trimmedVal = trimmedVal.replace(/^['"`]|['"`]$/g, '');
          // 尝试转换为数字
          if (!isNaN(trimmedVal) && trimmedVal !== '') {
            return Number(trimmedVal);
          }
          return trimmedVal;
        });

        if (tables[tableName]) {
          tables[tableName].data.push(values);
        }
        continue;
      }
    }

    return tables;
  };

  // 格式化数据为对象数组
  const formatTableData = (tableInfo) => {
    if (!tableInfo || !tableInfo.columns || !tableInfo.data) return [];

    return tableInfo.data.map(row => {
      const obj = {};
      tableInfo.columns.forEach((column, index) => {
        obj[column.name] = row[index] || '';
      });
      return obj;
    });
  };

  useEffect(() => {
    const initializeDatabase = async () => {
      try {
        setLoading(true);

        // 从服务器加载 SQL 文件
        const sqlContent = await loadSQLFile();

        // 解析 SQL 文件
        const parsedTables = parseSQLFile(sqlContent);
        setTables(parsedTables);

        // 设置默认选中的表
        const tableNames = Object.keys(parsedTables);
        if (tableNames.length > 0) {
          setSelectedTable(tableNames[0]);
          const tableInfo = parsedTables[tableNames[0]];
          setTableData(formatTableData(tableInfo));
          setTableHeaders(tableInfo.columns.map(col => col.name));
        }

        setLoading(false);
      } catch (err) {
        console.error('解析 SQL 文件失败，使用模拟数据:', err);
        // 使用模拟数据作为回退
        setTables(mockDatabaseTables);
        const tableNames = Object.keys(mockDatabaseTables);
        if (tableNames.length > 0) {
          setSelectedTable(tableNames[0]);
          setTableData(mockDatabaseTables[tableNames[0]]);
          setTableHeaders(Object.keys(mockDatabaseTables[tableNames[0]][0] || []));
        }
        setLoading(false);
      }
    };

    initializeDatabase();
  }, []);

  const handleTableChange = (tableName) => {
    setSelectedTable(tableName);
    if (tables[tableName] && tables[tableName].data) {
      // 解析的 SQL 数据格式
      const tableInfo = tables[tableName];
      setTableData(formatTableData(tableInfo));
      setTableHeaders(tableInfo.columns.map(col => col.name));
    } else {
      // 模拟数据格式
      setTableData(tables[tableName] || []);
      if (tables[tableName] && tables[tableName].length > 0) {
        setTableHeaders(Object.keys(tables[tableName][0]));
      }
    }
  };

  const handleUpload = () => {
    setSyncStatus('Uploading...');
    setTimeout(() => {
      setSyncStatus('Completed');
      setTimeout(() => setSyncStatus('Idle'), 2000);
    }, 3000);
  };

  const handleSyncSettings = () => {
    alert('打开同步设置...');
  };

  const handleViewReport = () => {
    alert(`生成 ${selectedTable} 表的详细报告...`);
  };

  if (loading) {
    return (
      <div className="data-owner-container">
        <div className="loading-spinner">正在加载数据库...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="data-owner-container">
        <div className="error-message">{error}</div>
      </div>
    );
  }

  return (
    <div className="data-owner-container">
      {/* 顶部导航栏 */}
      <div className="owner-header">
        <div className="header-left">
          <h1>CryptoDB</h1>
          <span className="role-badge">数据拥有者模式</span>
        </div>
        <button className="logout-btn" onClick={onLogout}>
          退出登录
        </button>
      </div>

      {/* 主内容区域 */}
      <div className="owner-content">
        {/* 左侧数据面板 */}
        <div className="data-panel">
          <div className="panel-header">
            <h2>YOUR DATA</h2>
            <div className="table-selector">
              <select 
                value={selectedTable} 
                onChange={(e) => handleTableChange(e.target.value)}
                className="table-dropdown"
              >
                {Object.keys(tables).map(tableName => (
                  <option key={tableName} value={tableName}>
                    {tableName}
                  </option>
                ))}
              </select>
            </div>
          </div>

          <div className="data-content">
            <div className="data-grid">
              <div className="data-row header-row">
                {tableHeaders.map(header => (
                  <div key={header} className="data-cell header-cell">
                    {header.toUpperCase()}
                  </div>
                ))}
              </div>

              {tableData.slice(0, 8).map((row, index) => (
                <div key={index} className="data-row">
                  {tableHeaders.map(header => (
                    <div key={header} className="data-cell">
                      {row[header]}
                    </div>
                  ))}
                </div>
              ))}
            </div>

            <div className="data-summary">
              <div className="summary-item">
                <label>表名:</label>
                <span>{selectedTable}</span>
              </div>
              <div className="summary-item">
                <label>记录数:</label>
                <span>{tableData.length}</span>
              </div>
              <div className="summary-item">
                <label>最后同步:</label>
                <span>{new Date().toLocaleDateString()}</span>
              </div>
            </div>

            <button className="view-report-btn" onClick={handleViewReport}>
              VIEW DETAILED REPORT
            </button>
          </div>
        </div>

        {/* 右侧操作面板 */}
        <div className="operations-panel">
          <div className="panel-header">
            <h2>CLOUD OPERATIONS</h2>
          </div>
          <div className="operations-content">
            <button className="upload-btn" onClick={handleUpload}>
              UPLOAD DATABASE TO CLOUD
            </button>
            <button className="settings-btn" onClick={handleSyncSettings}>
              SYNC SETTINGS
            </button>

            <div className="database-info">
              <h3>DATABASE INFO</h3>
              <div className="info-item">
                <label>表数量:</label>
                <span>{Object.keys(tables).length}</span>
              </div>
              <div className="info-item">
                <label>总记录:</label>
                <span>{Object.values(tables).reduce((sum, table) => {
                  if (Array.isArray(table)) {
                    return sum + table.length;
                  } else if (table && table.data) {
                    return sum + table.data.length;
                  }
                  return sum;
                }, 0)}</span>
              </div>
            </div>

            <div className="sync-status">
              <label>Sync Status:</label>
              <span className={`status-${syncStatus.toLowerCase().replace('...', '')}`}>
                {syncStatus}
              </span>
            </div>
            <div className="progress-bar">
              <div 
                className="progress-fill"
                style={{ 
                  width: syncStatus === 'Uploading...' ? '70%' : 
                         syncStatus === 'Completed' ? '100%' : '0%' 
                }}
              ></div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default DataOwnerPanel;
