import React, { useState, useEffect } from 'react';
import './DataOwnerPanel.css';



function DataOwnerPanel({ onLogout }) {
  const [tables, setTables] = useState({});
  const [selectedTable, setSelectedTable] = useState('');
  const [tableData, setTableData] = useState([]);
  const [tableHeaders, setTableHeaders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [syncStatus, setSyncStatus] = useState('Idle');

  // 从服务器加载 SQL 文件
  // 从 public/sql/ 加载 SQL 文件，比如 data_init.sql
const loadSQLFile = async () => {
  try {
    setLoading(true);
    const response = await fetch('/sql/data_init.sql'); // ✅ 你的目标 SQL 文件
    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }
    const sqlContent = await response.text();
    return sqlContent;
  } catch (err) {
    setError(`无法加载 SQL 文件: ${err.message}`);
    throw err;
  }
};

  // 解析 SQL 文件内容，返回 { 表名: { columns: [], data: [] } }
const parseSQLFile = (sqlContent) => {
  const tables = {};
  const lines = sqlContent.split('\n');
  let currentTable = null;
  let inCreateTable = false;
  let inInsert = false;

  for (const line of lines) {
    const trimmedLine = line.trim();

    // 跳过注释和无关语句
    if (!trimmedLine || trimmedLine.startsWith('--') || 
        trimmedLine.startsWith('/*') || trimmedLine.startsWith('LOCK TABLES')) {
      continue;
    }

    // 处理CREATE TABLE
    const createTableMatch = trimmedLine.match(/CREATE\s+TABLE\s+(?:IF\s+NOT\s+EXISTS\s+)?[`"]?(\w+) [`"]?(\([^)]*\))?/i);
    if (createTableMatch) {
      currentTable = createTableMatch[1];
      tables[currentTable] = {
        columns: [],
        data: []
      };
      // 提取列定义部分
      if (createTableMatch[2]) {
        const columnsDef = createTableMatch[2].replace(/[`()]/g, '').split(',');
        columnsDef.forEach(def => {
          const colMatch = def.trim().match(/`?(\w+)`?\s+(\w+)/);
          if (colMatch) {
            tables[currentTable].columns.push({
              name: colMatch[1],
              type: colMatch[2]
            });
          }
        });
      }
      continue;
    }

    // 处理INSERT INTO
    const insertMatch = trimmedLine.match(/INSERT\s+INTO\s+(?:`?(\w+)`?\s*)?\(?([^)]*)\)?\s+VALUES\s*\(([^)]*)\)/i);
    if (insertMatch && currentTable) {
      const tableName = insertMatch[1] || currentTable;
      if (!tables[tableName]) {
        tables[tableName] = { columns: [], data: [] };
      }

      // 解析值
      const valuesStr = insertMatch[3].replace(/\s+/g, '');
      const values = valuesStr.split(',')
        .map(v => v.replace(/^['"`]|['"`]$/g, '')
        .replace(/\\'/g, "'")  // 处理转义字符
        .replace(/\\\\/g, "\\")); // 处理转义字符

      // 将值转换为适当类型
      const typedValues = values.map(v => {
        if (!isNaN(v) && v !== '') return Number(v);
        if (v === 'true') return true;
        if (v === 'false') return false;
        if (v === 'NULL') return null;
        if ((v.startsWith("'") && v.endsWith("'")) || 
            (v.startsWith('"') && v.endsWith('"'))) {
          return v.substring(1, v.length - 1);
        }
        return v;
      });

      tables[tableName].data.push(typedValues);
      continue;
    }
  }

  return tables;
};
